mapper.select:
    SELECT o.*, t.testtypeno, t.testtype, t.testdgee, q.qbankno, q.refcourseno, q.reftesttypeno, q.refmemno, q.testscope, q.qustmt FROM question_bank q , question_bank_option o, test_type t


q.qbankno = o.refqbankno and q.reftesttypeno = t.testtypeno
 and q.qbankno = #{qbankno}  
 and q.refcourseno = #{refcourseno} 
 and t.reftesttypeno = #{testtypeno} 
 and q.refmemno = #{refmemno} 
 and q.testscope = #{testscope} 
 and q.qustmt LIKE CONCAT('%',#{qustmt},'%') 
 and q.qustatus = #{qustatus} 



     AND q.qbankno IN (SELECT s.qbankno FROM (SELECT * FROM question_bank q, test_type t WHERE q.reftesttypeno = t.testtypeno AND t.testdgee IN ('簡單') ORDER BY RAND() LIMIT 10) s) 
     AND q.qbankno IN (SELECT s.qbankno FROM (SELECT * FROM question_bank q, test_type t WHERE q.reftesttypeno = t.testtypeno AND t.testdgee IN ('簡單','中等') ORDER BY RAND() LIMIT 10) s) 
     AND q.qbankno IN (SELECT s.qbankno FROM (SELECT * FROM question_bank q, test_type t WHERE q.reftesttypeno = t.testtypeno AND t.testdgee IN ('簡單','中等','困難') ORDER BY RAND() LIMIT 10) s) 



        ORDER BY qbankno DESC

mapper.update:
update project.questionBank
refcourseno = #{refcourseno}, reftesttypeno = #{reftesttypeno}, refmemno = #{refmemno}, testscope = #{testscope}, qustmt = #{qustmt}, qustatus = #{qustatus}
where qbankno = #{qbankno}
